INSERT INTO `jkhn3_virtuemart_products_sr_yu` (`virtuemart_product_id`, `product_s_desc`, `product_desc`, `product_name`, `metadesc`, `metakey`, `customtitle`, `slug`) VALUES
(1, 'Autor bestselera Nikom ni reči i Nestao zauvek', '<p>Svet poznatih ličnosti i sportista sjajno je viviseciran i okrenut naglavačke u prvom krimiću bestseler autora Harlana Kobena.<br /><br />Njihov brak je tabloidni san, ali tek što su supermodel Lora Ejers i zvezda <em>Seltiksa</em> Dejvid Baskin rekli „da“, dogodila se tragedija. Dok su bili na medenom mesecu na Velikom koralnom grebenu u Australiji, Dejvid je otišao da pliva – i nije se vratio.<br /><br />Sada ožalošćena udovica, Lora ima hiljadu pitanja a nijedan odgovor. Njena potraga za istinom uvući će je u mrežu laži i obmane koja seže trideset godina unazad – dok na terenu u <em>Boston gardenu</em> jedan novajlija, čudo od košarkaša, igra svoju prvu važnu utakmicu...<br /><br />„Koben uporno izbacuje čitaoca iz ravnoteže inovativnim zapletima i đavolskim negativcima.“ <em>People</em><br /><br />„Pravo iznenađenje.“ <em>Entertainment Weekly</em><br /><br />„Pažnja će vam biti prikovana neizvesnošću u željnom iščekivanju raspleta.“ <em>New York Daily News</em></p>', 'Pravi se mrtav', '', '', '', 'pravi-se-mrtav'),
(2, 'Čuveno delo majstora kriminalističkih romana prvi put na srpskom jeziku.', '<p>Vagabund bez morala. Prelepa nezadovoljna žena. Problem za koji postoji samo jedno rešenje – iz kog iskrsnu novi problemi koje više niko ne može da reši.<br /><br />Frenk Čejmbers je skitnica nemirnog duha. Kora Papadakis je mlada lepotica koja vene u braku. Kad Frenka put nanese u Korino drumsko svratište, među njima će planuti strast koja će dovesti do kobnih posledica.<br /><br />Objavljen 1934. godine i zabranjen u Bostonu zbog svoje zapaljive mešavine nasilja i erotike, <em>Poštar uvek zvoni dvaput</em> pravo je remek-delo kriminalističkog žanra. Imao je brojne filmske adaptacije, od kojih je danas najpoznatija ona iz 1981, sa Džekom Nikolsonom i Džesikom Lang u glavnim ulogama. A svakako je najveću potvrdu dobio od Albera Kamija, koji je kazao da mu je <em>Poštar</em> bio nadahnuće za <em>Stranca</em>.<br /><br />„Dobra, brza, nasilna priča.“ Dešijel Hemet</p>', 'Postar uvek zvoni dvaput', '', '', '', 'postar-uvek-zvoni-dvaput'),
(3, 'Da li su mošti Svetog Save zaista spaljene na Vračaru?', '<p><em>Mit o lomači na Vračaru traje već četiri veka, kult svetitelja još i duže. Radovi na izgradnji Hrama Svetog Save uveliko se privode kraju. I šta sad?! Zamisli da se pojavi nešto što bi sve to obesmislilo. I šta bi na to rekla obična pastva?! Vekovima nam pričate jedno, a sad nešto posve drugo. Šta je istina? I čega još sve ima lažnog u vašim propovedima? Bilo bi nezgodno, zar ne?<br /></em><br />Arheolog Jovan Gligorijević je poginuo u saobraćajnoj nesreći. Pošto je bio na tragu nečeg epohalnog, njegov brat Goran i bivša devojka Vesna misle da je u pitanju ubistvo. Potraga za istinom vodiće ih do zaprepašćujućih otkrića i opasnih susreta, a dovešće ih i do aktera manje poznatog toka istorije... Da li se može tvrditi da nijedan patrijarh SPC u prošlom veku nije umro prirodnom smrću? Zašto vekovima postoje kantulovci, tajno bratstvo unutar SPC?<br /><br />Izuzetnom erudicijom i širokim potezima, uz veštu naraciju i mnoštvo likova, Vlada Arsić stvara jedinstveni triler, čija srž preispituje mit o moštima Svetog Save i postavlja mnoga pitanja u vezi sa našom sudbinom.</p>', 'Osinje gnezdo', '', '', '', 'osinje-gnezdo'),
(4, 'Više nema sveta koji poznajemo. Kakav će svet nastati umesto njega?', '<div id=\"knjiga-wrapper\" class=\"row\">\r\n<div id=\"sadrzaj\" class=\"col-xs-24 col-sm-16 col-md-14 col-lg-14 col-md-pull-6 col-lg-pull-6\">\r\n<div id=\"tabovi\">\r\n<div class=\"tab-content\">\r\n<div id=\"oknjizitab\" class=\"tab-pane active\">\r\n<div>Završnica serijala <em>Prolaz.</em><br /><br />Prošli ste <em>Prolazom</em>. Suočili ste se sa <em>Dvanaestoricom</em>. Sad stupite u <em>Grad ogledala</em> gde ćete prisustvovati konačnom obračunu. Dok bestseler epopeja hita prema zaprepašćujućoj završnici, Kroninova grupa preživelih čeka na drugi dolazak neopisive tame.<br /><br />Dvanaestorica su uništena i zastrašujuća stogodišnja vladavina mraka koji se spustio na svet okončana je. Preživeli izlaze van svojih zidina, odlučni da iznova sagrade društvo – i dovoljno smeli da sanjaju budućnost ispunjenu nadom.<br /><br />Ali daleko od njih, u mrtvoj metropoli, čeka on: Nulti. Prvi. Otac Dvanaestorice. Jad koji je mu je uništio ljudski život proganja ga, a mržnja izazvana njegovim preobražajem jarko plamti. Umiriće svoj bes samo ako uništi Ejmi – jedinu nadu čovečanstva, Devojčicu Niotkuda, koja je odrasla kako bi mu se suprotstavila.<br /><br />Poslednji put, sudariće se svetlost i tama, a Ejmi i njeni prijatelji konačno će saznati svoj usud.<br /><br />„<em>Grad ogledala</em> je poezija. Književno raskošno delo, jezički divno, čak i kada je sama akcija mračna i nasilna.“ <em>The Huffington Post</em><br /><br />„Ovo je uzbudljivo finale trilogije koja će ostati istaknuta kao jedno od velikih dostignuća američke fantastične proze.“ Stiven King</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<div class=\"karusel-wrapper\"> </div>', 'Grad ogledala', '', '', '', 'grad-ogledala'),
(5, 'IZA GENIJALNOSTI KRIJE SE LUDILO', '<p>„Vrtoglavo putovanje kroz krivudav, gasnim svetiljkama obasjan lavirint.“<br />Giljermo del Toro, režiser <em>Panovog lavirinta</em><br /><br /><em>Ova priča govori o Čarlsu Dikensu, mom prijatelju (ili makar nekadašnjem prijatelju) te o nesreći koja mu je oduzela duševni mir, zdravlje, a neki bi tu čak šapatom pribrojali i njegov razum...</em><br /><br />Naškrabanim rečima Vilkija Kolinsa, zapečaćenim stotinu dvadeset pet godina, započinje grozničavi silazak u podzemlje viktorijanskog Londona gde on biva uvučen u potragu Čarlsa Dikensa za utvarnom prilikom poznatom samo po imenu Drud.<br /><br />Njihova istraga odvešće ih u mračne lavirinte ćumeza, opijumskih jazbina, katakombi i kanalizacije. Ono što će otkriti uništiće njihovo prijateljstvo i oba pisca oterati do same ivice razuma.<br /><br />I UBISTVA...</p>', 'Drud - I deo', '', '', '', 'drud-i-deo'),
(6, 'Roman autora bestselera Teror i Drud.', '<p>Iz pera autora <em>Hiperiona</em>, dobitnika nagrade Hugo, izvire uznemirujuća priča o teroru i istinskom strahu.<br /><br />Leto je 1960. godine i neko ili nešto uhodi mlade ljude Elm Hejvena u Ilinoisu. Noćima se čuju čudni zvukovi, šapati, šuškanja. Za jednog dečaka sve počinje neobičnim, zastrašujućim glasom preko radija. Drugi imaju različita iskustva – neobjašnjiv nalet hladnog vazduha usred leta, neobjašnjiv strah od mraka. A viđaju i neobjašnjive prizore – razjapljene rupe u crvenoj zemlji, bizarni leš majmuna, vojnika iz Prvog svetskog rata bez lica koji se noću pojavljuje na prozoru.<br /><br />Kada se odavno zaboravljeno zvono oglasi usred najveće letnje tmine, ova deca znaju da ono označava kraj nevinosti i početak vladavine straha.<br /><br />„Ako <em>Leto noći</em> nije najbolji horor roman, onda je svakako jedan od tri najbolja – sjajna i zastrašujuća priča o pet dečaka koji se suočavaju sa jezivom i tajanstvenom pojavom tokom jednog leta. Ovo je jedna od onih retkih knjiga koje se moraju pročitati.“ <em>Stiven King</em><br /><br />„Priča od koje se ledi krv u žilama… Simonsova zastrašujuća vizija izvire iz njegove neverovatne sposobnosti da zakorači u taj strah koji svako dete poznaje i svaki čovek poriče; čudovište ispod kreveta, mrak u ormanu, neljudsko biće na prozoru… Ako ste plašljivi, nemojte kupiti ovu knjigu.“ <em>Los Angeles Daily News</em><br /><br />„Ostaje nam samo da se pitamo šta će Simons sledeće napisati, sad kad nam je pokazao da može ono što najbolji pisci horora i naučne fantastike mogu.“ <em>Philadelphia Inquirer</em><br /><br />„Zapanjujuće… spoj dobre proze i neizvesnosti u knjizi kojom Den Simons zaslužuje status autora bestselera poput Kinga i Kunca.“ <em>Denver Post</em></p>', 'Leto noći', '', '', '', 'leto-noći'),
(7, 'Dobitnik British Fantasy Award za najbolji horor roman godine i američke nagrade RUSA.', '<p>Oni te nikad ne ostavljaju...<br /><br />Godine 1975. u pustinji u Arizoni dogodio se masakr koji je šokirao svet. Hram poslednjih dana, kult čije su mistične tajne i paranormalna iskustva o kojima se govorkalo ležali skriveni iza prošlosti pune ubistava, seksualne izopačenosti i hapšenja, doživeo je svoj krvavi kraj posle jedne noći ritualnog nasilja. Ali priča time nije završena.<br /><br />Kajl Frimen je nezavisni filmski reditelj bez novca i bez mnogo izbora. Zato je ugrabio priliku kad mu je ponuđeno da snimi dokumentarac o zlokobnom kultu i njegovoj misterioznoj predvodnici.<br /><br />Dok putuje od Londona preko Pariza do Arizone, prateći trag Hrama poslednjih dana, progone ga neobični događaji, vantelesna iskustva, jezivi predmeti i noćni posetioci. Konačno otkriva užasne tajne zbog kojih je kult umro kako bi ih sačuvao ‒ ali da li je prekasno da pobegne od njegovog užasnog zaveštanja?<br /><br />„Ljubiteljima horor filmova dopašće se ovaj omaž ukletim mestima, bićima sa onog sveta i ritualima koji su krenuli po zlu.“ <em>Publishers Weekly</em><br /><br />„Vešto osmišljen i pametno napisan paranormalni horor o opsesiji i megalomaniji, seksu i moći.“ <em>Kirkus Reviews</em></p>', 'Kult', '', '', '', 'kult'),
(8, 'Nova biografija.', '<p>Ugledni istoričar filozofije pripoveda u dvadeset poglavlja đavolovu istoriju. Knjiga opisuje biblijske početke i demonologiju koju su crkveni oci istkali u srednjem veku, kobnu povezanost između Sotone i seksualnosti, verovanje u đavola i progon veštica. Kurt Flaš nas vodi u posetu paklu, posvećuje pažnju đavolovim saradnicima, anđelima zla, i prati njegovu istoriju sve do današnjice.<br /><br />Đavo se duboko mešao u čovekovu svakodnevicu dok mu plodovi prosvetiteljstva nisu uskratili moć. A ipak danas najrazličitije crkvene, teološke, pa i fundamentalističke grupe naglašavaju stvarnost njegove moći. Papa Jovan Pavle II je 1998. godine preradio i obnovio ritual isterivanja đavola iz 1614. Flaš u svojoj istoriji posebno ističe one mislioce koji svojim delima nisu jačali đavolovu moć, već su nastojali da ga skinu sa dodeljenog mu trona.<br /><br />„Kurt Flaš posredstvom đavola i njegovih preobražaja opisuje istoriju evropske kulture.“ <em>Neue Zürcher Zeitung</em><br /><br />„Izuzetno aktuelna knjiga i ujedno bezvremeno klasično delo.“ <em>Allgemeine Zeitung</em></p>', 'Đavo i njegovi anđeli', '', '', '', 'đavo-i-njegovi-anđeli'),
(9, 'Najpoznatija horor priča', '<p><em>Hladan znoj mi orosi čelo, zubi mi zacvokotaše a udovi se zgrčiše: pri bledoj i nejasnoj mesečevoj svetlosti ugledah strašno biće – bedno čudovište koje sam stvorio…</em><br /><br /><em>Frankenštajn ili Moderni Prometej</em> Meri Šeli jedan je od najčitanijih romana svih vremena. To je priča o Viktoru Frankenštajnu, naučniku koji je želeo da otkrije tajnu života a stvorio strašno čudovište. Ono je postalo otelotvorenje užasa i kazne koja sledi kad se prekorače moralne granice ljudskog napretka.<br /><br />Poput Prometeja, Viktor Frankenštajn je pobunjenik koji veruje u vlastite tvoračke potencijale i ima smelosti da prevaziđe ljudska ograničenja. Njegova intelektualna znatiželja i ambicija ne doprinose društvu već vode u propast i, kao njegov mitski preteča, on biva kažnjen zbog potrage za zabranjenim znanjem.<br /><br />Još od objavljivanja 1818. čitaoci su bili fascinirani Frankenštajnovim čudovištem i zaintrigirani moralnim i etičkim pitanjima koja je roman pokrenuo. Ovaj gotski roman i klasik engleske književnosti tako je prerastao u književni mit, koji i danas živi kroz mnogobrojne pozorišne, filmske i medijske adaptacije.</p>', 'Frankenštajn ili Moderni Prometej', '', '', '', 'frankenštajn-ili-moderni-prometej'),
(10, 'Vicevi i pitalice.', '<p>Ilustrovao Radič Mijatović.<br /><br />Zabavite se ponovo uz najbolje šale sa Malim Pericom u glavnoj ulozi, uz viceve o životinjama i šaljive školske grafite.<br /><br />Savet: Ako želite da prepisujete za vreme kontrolnog, samo nehajno ostavite ovu knjigu uz ivicu klupe. Nastavnik će se – provereno – uhvatiti za nju i do kraja časa neće obraćati pažnju na učenike.</p>', 'Otkacena skola 2', '', '', '', 'otkacena-skola-2'),
(11, 'Ovdje živi hrabra četa\r\nharambaše Jovančeta.', '<p>U bosanskom selu Lipovu odrasta grupa četvrtaka. Kada njihovu dobrodušnu učiteljicu Lanu zameni džangrizavi učitelj Paprika, koji veruje da je batina najbolje sredstvo za vaspitavanje đaka, dečaci, ali i jedna neustrašiva devojčica, odlučuju da se odmetnu i pobegnu u šumu. Tamo ubrzo niče logor Tepsija, u kojem će deca provesti mnoge bezbrižne dane, pune avantura. Međutim, počinje Drugi svetski rat i deca prevremeno odrastaju, shvatajući da se njihova borba protiv nepravde i nasilja nastavlja, ali ovog puta na novom i mnogo ozbiljnijem planu.<br /><br />Pišući o drugarstvu, hrabrosti, požrtvovanosti i ljubavi prema slobodi u teškim i neizvesnim istorijskim trenucima, Branko Ćopić je stvorio jedno od najvrednijih dela domaće književnosti za decu, čije poruke odzvanjaju i danas.<br /><br />„Ćopić je vrhovna mjera imaginacije, duha i morala našeg djetinjstva.“ Miljenko Jergović</p>', 'Orlovi rano lete', '', '', '', 'orlovi-rano-lete'),
(12, '„Ako se moji mali čitaoci iz ove priče nemaju čemu poučiti, izvesno je da će se imati čemu nasmejati.“', '<p><em>– Mogu li ja da odem u hajduke pa da otud, iz planine, napišem pismo načelniku: „Ako mi profesori poprave beleške, predaću se vlasti, a ako neće da mi poprave, ostaću i dalje hajduk.“ – Budalo jedna – reći će Čeda – pa kad si hajduk, šta će ti dobre beleške?<br /></em><br />Svakog četvrtka i nedelje grupica dečaka okuplja se pored hrastovog stabla na obali Dunava. Tu uživaju u dokolici, igraju se loptom, maštaju i izmišljaju različite priče umesto da rade domaće zadatke. Iz odbojnosti prema školi i obavezama rodiće se ideja da pobegnu od kuće i odmetnu se u hajduke. Ova nestašna avantura, puna zgoda i nezgoda, opametiće mlade hajduke i nasmejati čitaoce do suza.<br /><br /><em>Hajduci </em>su jedan od prvih humorističkih romana za decu u srpskoj književnosti, a i dan-danas spadaju u najčitanija i najpopularnija dela kako ovog autora tako i domaće književnosti za decu.<br /><br />„Ako se moji mali čitaoci iz ove priče nemaju čemu poučiti, izvesno je da će se imati čemu nasmejati.“<br /><br />Branislav Nušić</p>', 'Hajduci', '', '', '', 'hajduci'),
(13, 'Ilustrovao Aleksandar Zolotić.', '<div>Zamisli da ti upis u najčudniju, najzanimljiviju školu na svetu zavisi od toga da li ćeš rešiti sledeću zagonetku:<br /> </div>\r\n<div><em>Ono što se ovde<br />kô odgovor traži,<br />strašna, stara žena<br />drži u garaži.<br /><br /><br /></em></div>\r\n<div>Kako ti ide? Previše lako? Ma hajde, to je tek prva!<br /> </div>\r\n<div><br />E pa, u knjizi koju držiš u ruci nalazi se još tridesetak ovakvih zagonetnih prepreka, utkanih u otkačeni roman u zagonetkama Uroša Petrovića, sa čudesnim ilustracijama Aleksandra Zolotića. (Rešenja, koja se nalaze na kraju knjige mogu se čitati samo u ogledalu.)<br /> </div>\r\n<div>Pridruži se Lantanu i drugim mališanima u vratolomnoj i glavolomnoj trci za mesto u Domu za domišljatu decu <em>Ludi Šeširdžija, Martovski Zeka i pajac Alfred Kikli</em>!<br /><br />Da li bi tebi uspelo da se upišeš?<br />Postoji samo jedan način da to proveriš.<br />Znaš koji.<br />Potraga počinje.</div>', 'Zagonetna potraga', '', '', '', 'zagonetna-potraga'),
(14, 'Šetnja kroz svetsku istoriju u 80 rečenica!', '<div id=\"knjiga-wrapper\" class=\"row\">\r\n<div id=\"sadrzaj\" class=\"col-xs-24 col-sm-16 col-md-14 col-lg-14 col-md-pull-6 col-lg-pull-6\">\r\n<div id=\"tabovi\">\r\n<div class=\"tab-content\">\r\n<div id=\"oknjizitab\" class=\"tab-pane active\">„Umivam pravdom ruke svoje.“<br />Pontije Pilat<br /><br />„Zavadi, pa vladaj!“<br />Luj XI<br /><br />„Vreme je novac.“<br />Bendžamin Franklin<br /><br />„Posle nas potop!\"<br />madam De Pompadur<br /><br />Svako je nekada čuo ili pročitao ove rečenice. Ali u kakvom su istorijskom kontekstu izrečene? I ko ih je izgovorio?<br /><br />Ova knjiga vas vodi na jedno drugačije putovanje kroz svetsku istoriju. Na osnovu 80 poznatih citata iz perioda koji obuhvata 2600 godina, ona priča o ključnim istorijskim trenucima i oživljava pojedine epohe od antike do naših dana. Šta je za Rimsko carstvo značila kocka koju je bacio Cezar, šta je za reformaciju značila Luterova izreka, šta su Kantove reči govorile o prosvećenosti ili Kenedijeve o hladnom ratu...<br /><br />„Ova knjiga impresionira lakoćom s kojom autor uspeva da oživi događaje, ljude i slavna mesta.“ <em>Handelsblatt</em><br /><br />„Idealno delo za sve koji su propustili neki zanimljiv čas istorije i rado bi to nadoknadili.“ <em>Gießener Anzeiger</em></div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<div class=\"karusel-wrapper\"> </div>', 'Ipak se okreće', '', '', '', 'ipak-se-okreće'),
(15, 'Kratko, jasno, sveobuhvatno.', '<p>Autor daje širok pregled mnogih oblasti filozofije i upućuje čitaoca u filozofiju izlaganjem konkretnih primera kroz analizu Platonovih, Hjumovih, Dekartovih, Darvinovih dela i tekstova budističkih i indijskih mislilaca. Podučava nas kako da čitamo i tumačimo filozofiju, a da ne pristanemo na to da samo ovladamo nekim opštim činjenicama. Razmatra filozofiju Istoka, evropsku tradiciju mišljenja, kao i smisao i značaj bavljenja filozofijom. Raspravlja o tome kako se filozofija koristi u interesu određenih grupa – sveštenstva, žena i političkih aktivista. U knjizi se dotiču važne teme iz oblasti etike, teorije saznanja i problemi sopstva i identiteta. Objašnjava se na koji način su različite oblasti filozofije povezane, te se istražuje i kontekst u kojem su se ljudi bavili i bave se filozofijom.<br /><br />„Ovaj sažeti priručnik pokazuje da je filozofija i te kako zadivljujuća disciplina, otvorena za svaku temu i diskusiju i puna iznenađenja. Malo koja knjiga može da joj parira kad je reč o podsticanju zanimanja za filozofiju.“ <em>Philosopher’s Magazine</em><br /><br />O seriji: Sažeti priručnici koje objavljuje <em>Oxford University Press</em> predstavljaju savršen način da se brzo upoznate s određenom temom. Vrhunski autori kombinuju činjenice, analize, perspektive i nove ideje tako da čitaoci lako mogu da steknu jasan uvid u ono što ih interesuje. Serija je poznata širom sveta i s razlogom uživa ogromnu popularnost.</p>', 'Filozofija', '', '', '', 'filozofija'),
(16, 'Da li ste dovoljno bistri da budete agent KGB-a?', '<p>Najbolji način da razvijete svoje mentalne sposobnosti.<br /><br />Kada ljudi pomisle na reč špijun, najčešće zamišljaju različite visokotehnološke naprave, laserske olovke, brza kola, pištolje s prigušivačima i razorne eksplozije – ali najvažniji deo opreme koju ima svaki agent na ovom svetu je njegov mozak. Pamćenje je od vitalnog značaja za rad obaveštajaca. Potreba za potpunom tajnošću često ove operativce sprečava da bilo šta snimaju ili zapisuju, tako da oni moraju da se oslone isključivo na svoj mozak da bi zadržali i reprodukovali neverovatnu količinu informacija sa apsolutnom preciznošću.<br /><br />U ovoj knjizi ćemo vas naučiti kako da unapredite svoje pamćenje i izoštrite um pomoću niza vežbi razvijenih tokom mnogo godina, koje su korišćene za obuku najsposobnijih špijuna na svetu. Razvićete veštine testirane u najekstremnijim okruženjima i otključati sve šifre koje omogućuju punu sposobnost rada vašeg mozga.<br /><br />Prateći jednu obaveštajnu operaciju KGB-a, kroz mnoštvo zagonetki i logičkih zamki skrivenih u pravim dokumentima, konkretne zadatke svakog špijuna i vežbe uma koje koriste obaveštajne službe saznajte kako da unapredite svoje mentalne sposobnosti – moć zapažanja, snagu koncentracije, brzinu zaključivanja, snalaženje u prostoru i vrhunske operacije filtriranja i sistematizacije memorije.</p>', 'Škola za špijune', '', '', '', 'škola-za-špijune'),
(17, 'Zašto moramo da srušimo nehumani svetski poredak.', '<p><strong>Promeni svet: njemu je to potrebno! – glasi Brehtov zahtev.</strong><br /><br />Ratovi su se vratili. Glad i beda su čak i u Evropi ponovo deo svakodnevice. Prvi put u svojoj istoriji svet raspolaže resursima koji mogu da poraze glad, epidemije i tiraniju. Pa ipak, borba za raspoloživa dobra nečovečno se nastavlja, svirepije nego ikada.<br /><br />Žan Cigler, jedan od najznačajnijih svetskih sociologa koji već decenijama osuđuje nepravdu i ugnjetavanje, u ovoj knjizi postavlja suštinsko pitanje: zašto čovek u zapadnom potrošačkom društvu ne uspeva da zbaci lance koji ga sputavaju da slobodno misli i dela?<br /><br />Svetski poredak u kojem živimo je nečovečan. Jedan procenat najbogatijih ljudi danas poseduje isto onoliko imovine koliko i svi ostali stanovnici Zemlje. Cigler nas poziva da promenimo svet i doprinesemo stvaranju socijalnog poretka koji se ne zasniva na tiraniji i izrabljivanju.<br /><br />„U knjizi <em>Promeni svet!</em> Žan Cigler govori o najvažnijim globalnim temama i kao sociolog iznosi na videlo sve ono što društvo često svesno pokušava da sakrije.“ <em>Huffington Post<br /></em><br />„Žan Cigler nikada ne okoliša. Svoju borbu protiv siromaštva, tlačenja i gladi vodi provokativnim tvrdnjama, oštroumnim argumentima i nedvosmislenim predlozima kako da promenimo svet.“ <em>Süddeutsche Zeitung</em></p>', 'Promeni svet!', '', '', '', 'promeni-svet'),
(18, 'Autor bestselera Stiv Džobs i Ajnštajn.', '<p>„Prosvetljujuće i veličanstveno. Ovo je monumentalna pohvala titanskoj ličnosti.“ <em>Publishers Weekly</em><br /><br />Najkreativniji genije otkad je sveta i veka.<br /><br />Na osnovu hiljada stranica iz Leonardovih neverovatnih beležnica i na osnovu novih otkrića o njegovom životu i radu, Volter Ajzakson nam razotkriva velikog umetnika i naučnika.<br /><br />Leonardo je naslikao dve najslavnije slike u istoriji, <em>Tajnu večeru</em> i <em>Mona Lizu</em>. Ali je sebe podjednako smatrao naučnikom i tehnologom. Sa strašću koja je povremeno prelazila u opsesivnost, bavio se proučavanjem anatomije, fosila, ptica, letećih mašina, botanike, geologije i oružja. Sposobnost da ukršta društvene i prirodne nauke, koju simboliše crtež <em>Vitruvijev čovek</em>, stvorila je od njega najkreativnijeg inovatora u istoriji.<br /><br />Proučavao je matematiku optike, pokazao kako svetlosni zraci padaju na rožnjaču, i u <em>Tajnoj večeri</em> izveo iluzije izmenjene perspektive. Gulio je meso sa lica leševa, crtao mišiće koji pomeraju usne, a onda naslikao najpoznatiji osmeh u istoriji. Da Vinčijevo oduševljenje pozorištem uticalo je na njegove slike i izume.<br /><br />Leonardovo uživanje u kombinovanju raznorodnih interesovanja ostaje najbolji recept za kreativnost. Nije mu smetalo ni to što je bio neprilagođen: nezakoniti sin, homoseksualac, vegetarijanac, levoruk, rasejan, i ponekad jeretičan. Njegov život nas podseća na to koliko je važno da se usvojeno znanje dovodi u pitanje – da budemo maštoviti, strastveno radoznali, da razmišljamo drugačije i brižljivo posmatramo svet.<br /><br />„I sam Ajzakson je poput renesansnog čoveka. Kao i Leonarda, njega pokreće neiscrpna želja za otkrivanjem. Ta radost izbija na površinu u ovoj veličanstvenoj knjizi. U Ajzaksonu Leonardo dobija biografa kakvog zaslužuje – pisca sposobnog da shvati njegovu često mahnitu, čudnu potragu za poimanjem.“ <em>Times of London</em><br /><br />„Veličanstveno... Ajzakson još jednu kompleksnu, džinovsku figuru preobražava u nekoga ko nam je prepoznatljiv... Potpuno očaravajuće, majstorsko i strasno delo.“ <em>Kirkus Reviews</em></p>', 'Leonardo da Vinči', '', '', '', 'leonardo-da-vinči'),
(19, 'Priče o životu, smrti i neurohirurgiji.', '<p>Knjiga godine: <em>New York Times Book Review</em>, <em>Washington Post</em>, <em>Sunday Times</em>.<br /><br />„Opčinjavajući memoari o izuzetnoj karijeri, o usponima i padovima vrhunskog lekara.“ <em>Seattle Times</em><br /><br />Kako se oseća čovek kada drži nečiji život u svojim rukama, kada skalpelom zaseče tamo gde se stvaraju misli, osećanja, gde se nalaze razum i svest? Kako da živite sa posledicama operacije koja je nekome trebalo da spasi život, a sve ide naopako?<br /><br />Sa izuzetnim saosećanjem i iskrenošću vodeći britanski neurohirurg Henri Marš otkriva radost zbog uspeha, duboko emotivne trijumfe, zastrašujuće katastrofe, gorko žaljenje i trenutke crnog humora koji karakterišu život neurohirurga. <em>Ne nauditi </em>pruža nezaboravan uvid u bezbrojne ljudske drame koje se odvijaju u odnosu lekar – pacijent. Pre svega, to je lekcija o potrebi za nadom kada se suočavamo sa najtežim odlukama u životu.<br /><br />„Bolno iskren kada govori o greškama koje mogu uništiti mozak, veoma odmeren dok govori o napetim vezama između doktora i pacijenta, nemilosrdan prema birokratskim bolničkim upravama, Marš nam predstavlja umetnost vrhunske medicine i podiže naš duh. Izvanredno dostignuće.“ Ijan Makjuan<br /><br />„<em>Ne nauditi </em>nudi uvid u život doktora i dileme sa kojima se suočavaju dok mi s punim poverenjem polažemo svoje prevelike nade u njihove ruke.“ <em>Washington Post</em><br /><br />„Intimna, saosećajna i povremeno zastrašujuća knjiga. Autor piše sa neobičnim snagom i iskrenošću.“ <em>New York Times</em></p>', 'Ne nauditi', '', '', '', 'ne-nauditi');
